insert into MUSEUMS(mus_id, mus_name, mus_desc, mus_address, mus_coordinate, mus_phone, mus_open, categoty_id, period_id, exhibit_id, mus_avg_rate)
VALUES
	(1, 'Αρχαιολογικό Μουσείο Αλεξανδρούπολης', 'Στη μόνιμη έκθεσή του παρουσιάζονται ευρήματα που αφηγούνται την ιστορία της περιοχής από τους προϊστορικούς έως και τους ρωμαϊκούς χρόνους και προέρχονται από το χερσαίο τμήμα της Περιφερειακής Ενότητας Έβρου.', 'Λεωφόρος Μάκρης 44, Αλεξανδρούπολη 68131', geography::STPointFromText('POINT(40.8476801 25.8569979)', 4326), '(+30)2551026103)', 'από 1/11 έως 31/3 Δευτέρα, Τετάρτη, Πέμπτη, Παρασκευή, Κυριακή 08:30  15:30', 1, 8, 4, 5),
	(2, 'Αρχαιολογικό Μουσείο Αβδήρων', 'To Αρχαιολογικό Μουσείο Αβδήρων παρουσιάζει την εξέλιξη της πόλης των Αβδήρων από την ίδρυσή της τον 7ο αι. π.Χ. μέχρι τον 12ο αι. μ.Χ. Τα εκθέματά του μαρτυρούν τόσο τον τρόπο ζωής και τον πολιτισμό των αρχαίων Αβδηριτών όσο και τον ρόλο που διαδραμάτισε η πόλη στην περιοχή της Θράκης αλλά και στον αρχαίο ελληνικό κόσμο.', 'Δ. Λαζαρίδη 2, ’βδηρα 67061', geography::STPointFromText('POINT(40.9810199 24.9537921)', 4326),  '(+30) 2541051003)', 'Καθημερινά εκτός Κυριακή 08:30  15:30', 1, 8, 2, 3),
	(3, 'Αρχαιολογικό Μουσείο Δράμας', 'το Αρχαιολογικό Μουσείο Δράμας επιχειρεί να σκιαγραφήσει την πολιτιστική φυσιογνωμία της ευρύτερης περιοχής της Περιφερειακής Ενότητας Δράμας διαχρονικά, καθώς στις αίθουσές του παρουσιάζονται ευρήματα από τη μέση παλαιολιθική περίοδο (50.000 χρόνια από σήμερα) έως τους νεότερους χρόνους.', 'Πατριάρχου Διονυσίου 2, Δράμα 66133', 	geography::STPointFromText('POINT(41.1479538 24.1433015)', 4326), '(+30) 2521031365)', 'Καθημερινά εκτός Τρίτης 08:30  15:30', 1, 1, 3, 4),
	(4, 'Αρχαιολογικό Μουσείο Καβάλας', 'Στην έκθεσή του παρουσιάζεται η ιστορία της Καβάλας από την ίδρυσή της τον 7ο αι π.Χ. (Νεάπολη) έως το τέλος των βυζαντινών χρόνων (Χριστούπολη), καθώς και της ευρύτερης περιοχής της.', 'Ερυθρού Σταυρού 17, Καβάλα 65110', geography::STPointFromText('POINT(40.93570225 24.4037281081839)', 4326), '(+30) 2510222335', 'Καθημερινά εκτός Τρίτης 08:30  15:30', 1, 12, 9, 4),
	(5, 'Βυζαντινό Μουσείο Διδυμοτείχου', 'Το Βυζαντινό Μουσείο Διδυμοτείχου θέτει στο επίκεντρο της εκθεσιακής του αφήγησης το δίπολο Θράκη-Διδυμότειχο, παρουσιάζοντας μέσα από επιλεγμένα αντικείμενα τον ρόλο που διαδραμάτισαν στην ιστορία της βυζαντινής αυτοκρατορίας. Παράλληλα η έκθεση αναδεικνύει και την πολυπολιτισμικότητα της περιοχής, εστιάζοντας στον τρόπο ζωής και στις συνήθειες των ανθρώπων που την κατοίκησαν κατά τους νεότερους χρόνους.', 'Πλωτινουπόλεως και Σοφοκλέους, Διδυμότειχο 68300', geography::STPointFromText('POINT(41.3474704 26.497726475)', 4326), '(+30) 2553023960', 'από 1/11 έως 31/3 Δευτέρα, Τρίτη κλειστό. Τετάρτη, Πέμπτη, Παρασκευή & Κυριακή: 08:30  15:30. Σάββατο: 13:00  20:00.', 1, 12, 7, 4),
	(6, 'Εθνικό Αρχαιολογικό Μουσείο', 'Το Εθνικό Αρχαιολογικό Μουσείο ιδρύθηκε το 1893 και στεγάζεται σε νεοκλασικό κτήριο που οικοδομήθηκε μεταξύ των ετών 1866 και 1889. Είναι το μεγαλύτερο μουσείο της Ελλάδας και ένα από τα σημαντικότερα στον κόσμο. Οι πλούσιες συλλογές του, αριθμούν περισσότερα από 11.000 εκθέματα, είναι ένα πανόραμα του αρχαίου ελληνικού πολιτισμού από τις αρχές της προϊστορίας έως την ύστερη αρχαιότητα.', '28ης Οκτωβρίου (Πατησίων) 44, Αθήνα 10682',  geography::STPointFromText('POINT(37.98899787540997 23.733011484146118)', 4326), '(+30) 2132144800', 'Καθημερινά 09:00 - 15:00', 1, 8, 14, 5),
	(7, 'Αρχαιολογική Συλλογή Αεροδρομίου Αθηνών', 'Περιλαμβάνει ευρήματα που χρονολογούνται από την 3η χιλιετία π.Χ. έως και τη μεταβυζαντινή περίοδο παρουσιάζοντας την ιστορία των Μεσογείων, μιας κατεξοχήν αγροτικής περιοχής της Αττικής.', 'Διεθνής Αερολιμένας Αθηνών Ελευθέριος Βενιζέλος, Σπάτα, Αττική 1900', geography::STPointFromText('POINT(37.93732705 23.9465284918041)', 4326), '2103530000', 'Καθημερινά', 1, 2, 6, 5),
	(8, 'Αρχαιολογικό Μουσείο Λαυρίου', 'Το Αρχαιολογικό Μουσείο Λαυρίου φιλοξενεί ευρήματα από την ευρύτερη περιοχή της Λαυρεωτικής (Σούνιο, Θορικός) και από τα μεταλλευτικά - μεταλλουργικά εργαστήρια, αναδεικνύοντας πτυχές της ανθρώπινης ζωής και δραστηριότητας στην περιοχή από το 5000 π.Χ. έως τον 6ο αι. μ.Χ. Σημαντική θέση στο Μουσείο κατέχουν οι ανάγλυφες πλάκες από τη ζωφόρο του ναού του Ποσειδώνα στο Σούνιο, καθώς και επιτύμβια και αναθηματικά ανάγλυφα από τους αρχαίους δήμους της Λαυρεωτικής.', 'Ανδρέα Κορδέλλα 1, Λαύριο, Αττική 19500', geography::STPointFromText('POINT(37.7168338 24.05389062651)', 4326), '(+30) 2292022817', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 4, 3, 5),
	(9, 'Βυζαντινό και Χριστιανικό Μουσείο', 'Το Βυζαντινό και Χριστιανικό Μουσείο ιδρύθηκε το 1914 και από το 1930 στεγάζεται στη Villa Ilissia, κτηριακό συγκρότημα που ανεγέρθηκε το 1848 από τον αρχιτέκτονα Σταμάτιο Κλεάνθη ως κατοικία της Γαλλίδας Sophie de Marbois-Lebrun, δούκισσας της Πλακεντίας. Είναι ένα από τα σημαντικότερα μουσεία διεθνώς αφιερωμένο στην τέχνη και τον πολιτισμό των βυζαντινών και μεταβυζαντινών χρόνων. Διαθέτει περισσότερα από 25.000 αντικείμενα, οργανωμένα σε συλλογές, τα οποία χρονολογούνται από τον 3ο έως τον 20ο αιώνα μ.Χ.', 'Λεωφόρος Βασιλίσσης Σοφίας 22, Αθήνα 10675', geography::STPointFromText('POINT(37.974779813113656 23.74470055103302)', 4326), '(+30) 2132139500', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 12, 3, 5),
	(10, 'Μουσείο Ακρόπολης', 'Η παρουσίαση του πλούτου και της ποικιλίας των αρχιτεκτονικών γλυπτών και των ελεύθερων αναθημάτων της αρχαϊκής εποχής, όπως των διάσημων Κορών και των Ιππέων, ολοκληρώνεται με την παρουσίαση της ζωφόρου και των μετοπών του Παρθενώνα στην ομώνυμη αίθουσα', 'Διονυσίου Αρεοπαγίτου 15, Αθήνα 11742', geography::STPointFromText('POINT(37.9685667 23.7284816413335)', 4326), '(+30) 2109000900', 'Καθημερινά 09:00 - 21:00', 1, 2, 6, 5),
	(11, 'Αρχαιολογικό Μουσείο Πειραιά', 'To Αρχαιολογικό Μουσείο Πειραιά βρίσκεται κοντά στο λιμάνι της Ζέας. Στη μόνιμη έκθεσή του παρουσιάζονται ευρήματα που προέρχονται κυρίως από την ευρύτερη περιοχή του Πειραιά και από το νότιο παραλιακό μέτωπο της Αττικής. ', 'Χαριλάου Τρικούπη 31, Πειραιάς 18536', geography::STPointFromText('POINT(37.9371628 23.6441568843707)', 4326), '(+30) 2104590731', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 14, 8,4),
	(12, 'Αρχαιολογικό Μουσείο Λήμνου', 'Το Αρχαιολογικό Μουσείο Λήμνου στεγάζεται σε διώροφο κτίσμα του 19ου αι., που αρχικά λειτούργησε ως τουρκικό Διοικητήριο. o επισκέψιμος είναι προς το παρόν μόνον ο πρώτος όροφος όπου παρουσιάζονται ευρήματα από τους προϊστορικούς οικισμούς του νησιού, καθώς και γλυπτά και επιγραφές ιστορικών χρόνων από τη Λήμνο, την Ίμβρο και την Τένεδο.', 'Ναυάρχου Κουντουριώτη, Ρωμέικος Γυαλός, Μύρινα 81400', geography::STPointFromText('POINT(39.8802573 25.0623226)', 4326), '(+30) 2254022990', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 11, 7, 4),
	(13, 'Αρχαιολογικό Μουσείο Μυτιλήνης (Νέο κτήριο)', 'Το Αρχαιολογικό Μουσείο Μυτιλήνης (Νέο κτήριο) εγκαινιάστηκε το 1999 και η έκθεσή του είναι αφιερωμένη στη Λέσβο της ελληνιστικής και ρωμαϊκής περιόδου, κατά τη διάρκεια της οποίας το νησί γνώρισε ιδιαίτερη ανάπτυξη. Ψηφιδωτά δάπεδα, τοιχογραφίες και άλλα ευρήματα προερχόμενα από ρωμαϊκές επαύλεις, καθώς και δείγματα γλυπτικής παρουσιάζονται στις αίθουσές του.', '8ης Νοεμβρίου-Κιόσκι, Μυτιλήνη 81100', geography::STPointFromText('POINT(39.106792 26.5615094393943)', 4326),  '(+30) 2251040223', 'Καθημερινά 09:00 - 21:00', 1, 10, 9, 4),
	(14, 'Αρχαιολογικό Μουσείο Σάμου', 'Το Αρχαιολογικό Μουσείο Σάμου στεγάζεται σε δύο κτήρια, το παλαιό, διώροφο νεοκλασικό κτήριο που οικοδομήθηκε το 1912, και το νέο, κτήριο που οικοδομήθηκε το 1984. Στο παλαιό κτήριο εκτίθενται ευρήματα από το Ηραίο της Σάμου (αγάλματα, αγγεία, αφιερώματα από χαλκό, ελεφαντόδοντο, ξύλο κ.λπ.), ενώ το νέο στεγάζει τη γλυπτοθήκη του μουσείου, με σημαντικά δείγματα αρχαϊκής περιόδου.', 'Πλατεία Δημαρχείου, Βαθύ 83100', geography::STPointFromText('POINT(37.7544313936541 26.978890299411916)', 4326), '(+30) 2273027469', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 8, 10, 5),
	(15, 'Αρχαιολογικό Μουσείο Χίου', 'Παρουσιάζονται ευρήματα προερχόμενα από τους προϊστορικούς οικισμούς του νησιού, χαρακτηριστικά έργα του περίφημου χιώτικου εργαστηρίου γλυπτικής, επιγραφές, νομίσματα, πλούσια συλλογή αγγείων, ευρήματα από τα μεγάλα ιερά, ψηφιδωτά δάπεδα και επιτύμβιες στήλες.', 'Μιχάλων 10, Χίος 82100', geography::STPointFromText('POINT(38.3651338 26.1390513)', 4326), '(+30) 2271044239', 'Από 13-5-2024 θα παραμείνει κλειστό μέχρι νεωτέρας.', 1, 4, 9, 4),
	(16, 'Αρχαιολογικό Μουσείο Αγρινίου', 'Φιλοξενεί ευρήματα που προέρχονται από τους κυριότερους αρχαιολογικούς χώρους της Αιτωλοακαρνανίας, μεταξύ των οποίων το Ηρώο της Αλυζίας και το Ηρώο της Καλυδώνας, και χρονολογούνται από τους προϊστορικούς χρόνους έως τη ρωμαϊκή κατάκτηση.', 'Διαμαντή και Καζαντζάκη 1, Αγρίνιο 30131', 	geography::STPointFromText('POINT(38.63066225 21.4091457324419)', 4326), '(+30) 2641027377', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 7, 13, 4),
	(17, 'Αρχαιολογικό Μουσείο Ολυμπίας', 'φιλοξενεί στις αίθουσές του τα ευρήματα που συνδέονται με το Ιερό της Ολυμπίας, ενός από τα μεγαλύτερα και σημαντικότερα θρησκευτικά και λατρευτικά κέντρα της αρχαιότητας. Μεταξύ αυτών περιλαμβάνονται λαμπρά έργα της αρχαίας ελληνικής τέχνης, όπως ο Ερμής του Πραξιτέλη, η Νίκη του Παιωνίου αλλά και ο γλυπτός διάκοσμος από τον ναό του Διός.', 'Αρχαία Ολυμπία 27065', geography::STPointFromText('POINT(37.64351605 21.6294706408377)', 4326), '(+30) 2624022742', 'Καθημερινά 09:00 - 21:00', 1, 5, 6, 5),
	(18, 'Αρχαιολογικό Μουσείο Πύργου', 'Η κοινωνία, η εκπαίδευση, η οικονομία, το εμπόριο και οι επαγγελματικές ασχολίες, τα ταφικά έθιμα των κατοίκων της περιοχής της σημερινής Ηλείας παρουσιάζονται στον επισκέπτη μέσα από ευρήματα που χρονολογούνται από τους προϊστορικούς έως και τους νεότερους χρόνους.', 'Καραϊσκάκη 10, Πύργος 27131', geography::STPointFromText('POINT(37.67138285 21.4406156188737)', 4326), '(+30) 2621020475', 'από 1/11 έως 31/3 Καθημερινά 09:00 - 21:00', 1, 10, 14, 5),
	(19, 'Ξενοκράτειο Αρχαιολογικό Μουσείο Ιεράς Πόλεως Μεσολογγίου', 'Στη μόνιμη έκθεσή του παρουσιάζονται περισσότερα από 1.200 αντικείμενα που καλύπτουν χρονολογικά τις περιόδους από τους προϊστορικούς έως και τους ύστερους ρωμαϊκούς χρόνους (100.000 π.Χ.  3ος αι. μ.Χ.) και προέρχονται από το σύνολο της Π.Ε. Αιτωλοακαρνανίας.', 'Ασ. Φωτήλα 2', geography::STPointFromText('POINT(38.37009276166383 21.427660882472995)', 4326), '(+30) 2631026068', 'από 1/11 έως 31/3 Καθημερινά 09:00 - 17:00', 1, 9, 13, 5),
	(20, 'Αρχαιολογική Συλλογή Δισπηλιού Καστοριάς', 'Η Αρχαιολογική Συλλογή Δισπηλιού είναι αφιερωμένη στον λιμναίο νεολιθικό οικισμό του Δισπηλιού Καστοριάς. Σε αυτήν παρουσιάζονται θέματα που αφορούν στην Οργάνωση του χώρου, στην Οικονομία-Τεχνολογία και στην Ιδεολογία των νεολιθικών γεωργοκτηνοτρόφων.', 'Δισπηλιό, Καστοριά 52057', geography::STPointFromText('POINT(40.48481105 21.292550495852)', 4326), '(+30) 2467085765', 'από 1/11 έως 31/3 Καθημερινά 09:00 - 21:00', 1, 2, 4, 3),
	(21, 'Αρχαιολογική Συλλογή Κοζάνης', 'Στην Αρχαιολογική Συλλογή Κοζάνης εκτίθενται παλαιά και νέα ανασκαφικά σύνολα και συλλογές, συμπεριλαμβανομένων και ευρημάτων των μεγάλων σωστικών ανασκαφών της Περιφερειακής Ενότητας Κοζάνης, σκιαγραφώντας την ιστορία της περιοχής από τους προϊστορικούς έως και τους ρωμαϊκούς χρόνους.', 'Δημοκρατίας 8, Κοζάνη 50131', geography::STPointFromText('POINT(40.30228157225952 21.785558760166168)', 4326),  '(+30) 2461026210', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 11, 12, 4),
	(22, 'Αρχαιολογικό Μουσείο Φλώρινας', 'Το Αρχαιολογικό Μουσείο Φλώρινας περιλαμβάνει αντιπροσωπευτικά ευρήματα ανασκαφών από αρχαιολογικές θέσεις της Περιφερειακής Ενότητας Φλώρινας, καλύπτοντας μια μακρά περίοδο από τους νεολιθικούς έως και τους βυζαντινούς χρόνους.', 'Σιδηροδρομικού Σταθμού 8, Φλώρινα 53100', geography::STPointFromText('POINT(40.78076515 21.4137781079334)', 4326), '(+30) 2385028206', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 11, 11, 4),
	(23, 'Αρχαιολογικό Μουσείο ’ρτας', 'Η έκθεση του Αρχαιολογικού Μουσείου ’ρτας είναι αφιερωμένη στην ίδρυση, την ακμή και την καταστροφή της Αμβρακίας, της κορινθιακής αποικίας που βρίσκεται θαμμένη κάτω από τη σύγχρονη πόλη της ’ρτας. Παρουσιάζει την ιστορία της μέσα από το δημόσιο και ιδιωτικό βίο των Αμβρακιωτών, τα νεκροταφεία της πόλης, αλλά και το «τέλος» της στα ρωμαϊκά χρόνια.', 'Περιοχή «Τρίγωνο» ’ρτας, ’ρτα 47100', geography::STPointFromText('POINT(39.14952335 20.9793951319486)', 4326), '(+30) 2681021191', 'από 1/11 έως 31/3 Καθημερινά 09:00 - 21:00', 1, 11, 14, 4),
	(24, 'Αρχαιολογικό Μουσείο Ηγουμενίτσας', 'Η μόνιμη έκθεση του Αρχαιολογικού Μουσείου Ηγουμενίτσας περιλαμβάνει εκθέματα που καλύπτουν, χρονολογικά, μία ιδιαίτερα μεγάλη περίοδο ανθρώπινης παρουσίας στον θεσπρωτικό χώρο, από τη μέση παλαιολιθική περίοδο (100.000 χρόνια πριν από σήμερα) έως τους χρόνους της οθωμανικής κυριαρχίας, με το ενδιαφέρον να επικεντρώνεται στην ελληνιστική εποχή, περίοδο άνθησης για τις θεσπρωτικές πόλεις.', '28ης Οκτωβρίου 2, Ηγουμενίτσα 46100', geography::STPointFromText('POINT(39.51143815 20.2587941260403)', 4326), '(+30) 2665028539', 'Καθημερινά 09:00 - 16:00', 1, 7, 8, 4),
	(25, 'Αρχαιολογικό Μουσείο Ιωαννίνων', 'Περιλαμβάνει ευρήματα από όλη την Ήπειρο, καλύπτοντας μια χρονική περίοδο από την κατώτερη παλαιολιθική εποχή (250.000 χρόνια πριν) έως τους ύστερους ρωμαϊκούς χρόνους. Ιδιαίτερη έμφαση δίνεται στα ευρήματα από το ιερό της Δωδώνης, ένα από τα σπουδαιότερα μαντεία του αρχαίου ελληνικού κόσμου.', 'Πλατεία 25ης Μαρτίου 6, Ιωάννινα 45221', geography::STPointFromText('POINT(39.66666475 20.8560010015229)', 4326), '(+30) 2651001089', 'από 1/11 έως 31/3 Καθημερινά 08:30  15:30, Τρίτη κλειστό', 1, 1, 9, 5),
	(26, 'Μουσείο της Ιστορίας των Ολυμπιακών Αγώνων', 'Το Μουσείο της Ιστορίας των Ολυμπιακών Αγώνων της Αρχαιότητας φιλοξενεί πάνω από 400 αρχαία έργα, που προέρχονται από το Ιερό του Διός της Ολυμπίας, αλλά και από άλλα Μουσεία της ελληνικής επικράτειας. Τα εκθέματα καλύπτουν μεγάλο χρονολογικό φάσμα, από τη δεύτερη χιλιετία π.Χ. έως και τον 5ο αι. μ.Χ. Τα αρχαία έχουν εκτεθεί κατά θεματικές ενότητες και μέσα από αυτά παρουσιάζεται η υπερχιλιετής ιστορία των Ολυμπιακών αγώνων, του μακροβιότερου θεσμού της αρχαιότητας.', '', geography::STPointFromText('POINT(37.64105456051856 21.625544080459278)', 4326), '30 26240 29119', 'Καθημερινά (Δευτέρα - Κυριακή): 08:30 - 15.30', 10, 4, 2, 5),
	(27, 'Ναυτικό Μουσείο Ελλάδoς', 'Στος 10 μεγάλες αίθουσες που έχουν συνολική επιφάνεια περίπου 1850 τ.μ. παρουσιάζεται με ανάγλυφο τρόπο η ναυτική πορεία του ελληνισμού μέσα στους αιώνες. Στην πρώτη στεγάζεται η Ναυτική Πινακοθήκη, με έργα των σημαντικότερων θαλασσογράφων του 19ου και 20ου αιώνα (Προσαλέντη-Βολανάκη-Χατζή κ.ά.). Στη συνέχεια παρουσιάζεται η ναυτική δραστηριότητα των Ελλήνων στην προϊστορία, στην αρχαιότητα, στη βυζαντινή και μεταβυζαντινή περίοδο ως το τέλος του 18ου αιώνα, στους Βαλκανικούς Πολέμους και από τον Β Παγκόσμιο Πόλεμο έως τις μέρες μας. Παράλληλα, δίνεται έμφαση στη μετάβαση από την ιστιοφόρο ναυτιλία στην ατμοπλοΐα. Οι τελευταίες εκθεσιακές ενότητες αφορούν την ιστορία και εξέλιξη του εμπορικού ναυτικού.', 'Ακτή Θεμιστοκλέους, Φρεαττύς, Τ.Κ. 185 37, Πειραιάς (Νομός Αττικής)', geography::STPointFromText('POINT(37.92815220845 23.63789)', 4326), '+30 210 4516264, 4286959', 'Δευτέρα, Κυριακή και αργίες κλειστά. Τρίτη - Σάββατο: 09.00 - 14.00', 8, 15, 12, 4),
	(28, 'Ναυτικό Μουσείο Αιγαίου', 'Στα εκθέματα του Μουσείου περιλαμβάνονται ομοιώματα πλοίων από την προ-Μινωϊκή περίοδο μέχρι τις αρχές του 20ού αιώνα, ιστορικά ναυτιλιακά έγγραφα, σπάνια χαρακτικά και χάρτες, αρχαία αντικείμενα, ναυτικά όργανα, εξαρτήματα και εργαλεία, καθώς και νομίσματα με ναυτικά θέματα από τον 5ο αιώνα π.Χ..', 'Ενόπλων Δυνάμεων 10, Μύκονος (Νομός Κυκλάδων)', geography::STPointFromText('POINT(37.44527903074 25.32839621717)', 4326), '+30 22890 22700 , 210 8125547', 'Από 1η Απριλίου έως 30 Νοεμβρίου Τρίτη με Κυριακή: 10:00-13:00 & 18:00  21:00', 8, 15, 12, 4),
	(29, 'Νομισματικό Μουσείο', 'Το Νομισματικό Μουσείο είναι ένα από τα παλαιότερα κρατικά μουσεία της Ελλάδος. Θεσμοθετήθηκε το 1834, την ίδια χρονιά με το Εθνικό Αρχαιολογικό Μουσείο. Η αρχαιολατρία της εποχής διαδεδομένη σε όλη την Ευρώπη και η πρόσφατη ίδρυση του νεοελληνικού κράτους δημιούργησαν τις συνθήκες της προστασίας της εθνικής πολιτιστικής κληρονομιάς. Έτσι, από την αρχή, η ιστορία του Μουσείου συνδέθηκε άμεσα με την ιστορία του νεοελληνικού κράτους, τις κοινωνικές συνθήκες και τις πολιτιστικές κατευθύνσεις της κάθε εποχής. Καθοριστικό ρόλο στην πορεία του όμως, έπαιξε και η προσωπικότητα των ανθρώπων που το διεύθυναν.', 'Ελ. Βενιζέλου 12, Αθήνα 106 71', geography::STPointFromText('POINT(37.97775645 23.735391134)', 4326), '+30 2103632057', 'Από 1 Νοεμβρίου 2024 έως 31 Μαρτίου 2025 Τετάρτη  Δευτέρα: 08.30  15.30<br>Τρίτη κλειστό', 11, 14, 5, 3),
	(30, 'Μουσείο Επιστημών και Τεχνολογίας Πανεπιστημίου Πατρών', 'Το ΜΕΤ ασχολείται με τη συγκρότηση συλλογών και την τεκμηρίωσή τους με σκοπό την αξιοποίησή τους σε εκθεσιακούς χώρους αλλά και εκπαιδευτικά προγράμματα. Η τεκμηρίωση των συλλογών περιλαμβάνει ταύτιση και περιγραφή κάθε αντικειμένου, καθώς και στοιχεία σχετικά με την προέλευσή τους. Το υλικό το οποίο συγκροτεί και εμπλουτίζει τις συλλογές του Μουσείου αποτελείται από σκεύη, εργαλεία, μηχανήματα, τεχνολογικούς εξοπλισμούς, αρχειακό και φωτογραφικό υλικό, χάρτες, βιβλία και άλλο συνοδευτικό υλικό. Το υλικό του Μουσείο προέρχεται κυρίως από δωρεές. Για τον εμπλουτισμό των συλλογών το ΜΕΤ είναι σε επαφή με φορείς οι οποίοι μπορούν να βοηθήσουν. Καλεί επίσης όλους τους φορείς και τα άτομα που έχουν στην κατοχή τους σχετικό υλικό ή γνωρίζουν την ύπαρξη του να συμβάλλουν στην προσπάθεια του Μουσείου.', 'Πανεπιστημιούπολη Πατρών 265 04', geography::STPointFromText('POINT(38.28777600591 21.78474302675)', 4326), '', 'Δευτέρα - Παρασκευή 9:00 - 14:00', 12, 15, 8, 5),
	(31, 'Κέντρο Ιστορικής Διαδρομής Εβραϊσμού Θεσσαλονίκης', 'Το Κέντρο περιλαμβάνει εξειδικευμένη βιβλιοθήκη με μελέτες για την ιστορία, λαογραφία, γλωσσολογία του εβραϊσμού της Ελλάδος. Επίσης δισκοθήκη-κασσετοθήκη με λαϊκά και δημοτικά τραγούδια των Ελλήνων Εβραίων. Ακόμη διαρκή έκθεση 300 φωτογραφιών και χαρτών της περιόδου 1880-1943, με θέματα από την ζωή των Εβραίων της Θεσσαλονίκης, και μικρή πτέρυγα μουσείου, όπου εκτίθενται εβραϊκά λατρευτικά αντικείμενα, παραδοσιακές ενδυμασίες, παλαιές εβραϊκές εφημερίδες και εκδόσεις.', 'Βασ. Ηρακλείου 26, Τ.Κ. 54624, Θεσσαλονίκη (Νομός Θεσσαλονίκης)', geography::STPointFromText('POINT(40.63450691337 22.94146102366860)', 4326), '+30 2310 2279063, 223231', 'Από 1η Νοεμβρίου έως 31 Μαρτίου 2010  08:30-15:00 ', 12, 14, 7, 5),
	(32, 'Λευκός Πύργος: Μουσείο', 'Ο Λευκός Πύργος, το εμβληματικό μνημείο της Θεσσαλονίκης, φιλοξενεί από τις 5 Σεπτεμβρίου 2008 έκθεση αφιερωμένη στη Θεσσαλονίκη, την πόλη που δεν έπαψε ποτέ να είναι πόλη στη μακραίωνη ιστορική παρουσία της και που ήταν ανοιχτή στη συνύπαρξη ανθρώπων με διαφορετική καταγωγή, θρησκεία και κουλτούρα. Η έκθεση διατρέχει συνοπτικά και με πανοραμική ματιά την ιστορία της πόλης από την ίδρυσή της το 316/15 π.Χ. μέχρι τις μέρες μας. Ο στόχος της δεν είναι να υποκαταστήσει τα άλλα μουσεία της πόλης, αντιθέτως, φιλοδοξεί να «συστήσει» τη Θεσσαλονίκη κυρίως στους κατοίκους της και να κεντρίσει το ενδιαφέρον τους για παραπέρα γνωριμία μέσα από τα θεματικά μουσεία και τα μνημεία της.', 'Παλαιά παραλία Θεσσαλονίκης, Θεσσαλονίκη (Νομός Θεσσαλονίκης)', geography::STPointFromText('POINT(40.62636539360450  22.948334258804)', 4326), '2310267835', 'Από 1η Νοεμβρίου έως 31 Μαρτίου 2010  08:30-15:00', 12, 14, 4, 5),
	(33, 'Μουσείο και Κέντρο Μελέτης του Ελληνικού Θεάτρου', 'Στις αίθουσες του εκτίθενται τεκμήρια της θεατρικής ζωής, σκηνικά κοστούμια και σκηνικά αντικείμενα, φωτογραφίες, μακέτες, προσωπικά αντικείμενα σημαντικών ανθρώπων του θεάτρου, αφίσες και προγράμματα. Η βιβλιοθήκη περιλαμβάνει περί τους 25.000 τόμους για το θέατρο από την ελληνική και ξένη βιβλιογραφία. Το δε θεατρικό αρχείο, χειρόγραφα του 18ου αιώνα, προγράμματα και αφίσες από θιάσους από τον 19ο αιώνα.', 'Ακαδημίας 50, Τ.Κ. 10679, Αθήνα (Νομός Αττικής)', geography::STPointFromText('POINT(37.98121474553 23.734932700022)', 4326), '+30 210 3629430, 3637453', 'Από 1η Νοεμβρίου έως 31 Μαρτίου 2010: 08:30-15:00', 9, 15, 2, 4),
	(34, 'Μουσείο της Ελιάς και του Ελληνικού Λαδιού', 'Το Μουσείο της Ελιάς και του Ελληνικού Λαδιού στη Σπάρτη έχει ως κύριο σκοπό να αναδείξει τον πολιτισμό και την τεχνολογία της ελιάς και της ελαιοπαραγωγής, που συνδέεται άρρηκτα με την ελληνική, και γενικότερα τη μεσογειακή ταυτότητα. Είναι μοναδικό στο είδος του στην Ελλάδα και βρίσκεται στην καρδιά της Λακωνίας, μιας από τις κύριες ελαιοπαραγωγούς περιοχές της χώρας μας.', 'Όθωνος - Αμαλίας 129, Τ.Κ. 23100, Σπάρτη (Νομός Λακωνίας)', geography::STPointFromText('POINT(37.07058133628 22.4262401929262)', 4326),	'+30 27310 89315, +30 210 3256922-3', '16 Οκτωβρίου-28 Φεβρουαρίου: 10.00- 17.00 1η Μαρτίου-15 Οκτωβρίου: 10:00-18:00', 11, 15, 4, 3),
	(35, 'Αρχαιολογικό Μουσείο Καστελλόριζου', 'Το Αρχαιολογικό Μουσείο Καστελλόριζου στεγάζεται από το 1984 στο "Κονάκι" που αποτελεί τμήμα του μεσαιωνικού Κάστρου του Αγ. Νικολάου. Βρίσκεται στη συνοικία "Κάβος" και πρόκειται για διώροφο κτίσμα, του οποίου το ισόγειο ανάγεται στα χρόνια της Ιπποτοκρατίας, ενώ ο όροφος αποτελεί προσθήκη του 19ου αι., εποχή Τουρκοκρατίας.', 'Μεγίστη (Νομός Δωδεκανήσου)', geography::STPointFromText('POINT(36.151557010643 29.593552924569)', 4326), '+30 22460 49283', 'Τετάρτη ως και Δευτέρα 08:30-15:30', 2, 12, 4, 5),
	(36, 'Βυζαντινή και Μεταβυζαντινή Συλλογή Χανίων', 'Το πλούσιο αρχαιολογικό υλικό, το οποίο έχει συγκεντρωθεί από τις ανασκαφές που επί πολλά χρόνια διενεργεί η 13η Εφορεία Βυζαντινών Αρχαιοτήτων στο νομό Χανίων, αλλά και από περισυλλογές και δωρεές, αποτέλεσε μια Συλλογή στην οποία καταγράφεται με σαφήνεια η ιστορική πορεία του δυτικότερου νομού της Κρήτης από τα παλαιοχριστιανικά χρόνια έως και την τουρκοκρατία. Αντιπροσωπευτικά δείγματα αυτής της Συλλογής εκτίθενται στο ναό του San Salvatore.', 'Θεοτοκοπούλου 78, Τ.Κ. 73131, Χανιά (Νομός Χανίων)', geography::STPointFromText('POINT(35.5184569330556 24.015186969686)', 4326), '+30 28210 96046', 'Από 1η Νοεμβρίου 2024 έως την 31η Μαρτίου 2025 08:30-15:30', 2, 12, 13, 4),
	(37, 'Μουσείο Γουλανδρή Φυσικής Ιστορίας', 'Το Μουσείο Γουλανδρή Φυσικής Ιστορίας (Μ.Γ.Φ.Ι.) ιδρύθηκε το 1964 από τον ’γγελο Γουλανδρή και τη Νίκη Γουλανδρή, αποτελεί το πρώτο μουσείο φυσικής ιστορίας στην Ελλάδα και τον πρώτο ελληνικό φορέα που καθιέρωσε την περιβαλλοντική εκπαίδευση.', 'Λεβίδου 13 και Όθωνος 100, 145 62 Κηφισιά', geography::STPointFromText('POINT(38.07437 23.81466)', 4326), '210 8015870', 'Καθημερινά 09:00 - 16:00', 6, 13, 5, 6);
