VALUES
    ('Μουσείο της Ιστορίας των Ολυμπιακών Αγώνων',
     'Το Μουσείο της Ιστορίας των Ολυμπιακών Αγώνων της Αρχαιότητας φιλοξενεί πάνω από 400 αρχαία έργα...',
     geography::STPointFromText('POINT(29.119 30.26240)', 4326),
     
     '(+30) 26240 23964',
     'Καθημερινά: 08:30 - 15:30',
     10, 4, 5, 2),


insert into MUSEUMS(mus_name, mus_desc, mus_coordinate, mus_phone, mus_open, categoty_id, period_id, mus_avg_rate, exhibit_id)
values
	('Μουσείο της Ιστορίας των Ολυμπιακών Αγώνων', 'Το Μουσείο της Ιστορίας των Ολυμπιακών Αγώνων της Αρχαιότητας φιλοξενεί πάνω από 400 αρχαία έργα, που προέρχονται από το Ιερό του Διός της Ολυμπίας, αλλά και από άλλα Μουσεία της ελληνικής επικράτειας. Τα εκθέματα καλύπτουν μεγάλο χρονολογικό φάσμα, από τη δεύτερη χιλιετία π.Χ. έως και τον 5ο αι. μ.Χ. Τα αρχαία έχουν εκτεθεί κατά θεματικέςενότητες και μέσα από αυτά παρουσιάζεται η υπερχιλιετής ιστορία των Ολυμπιακών αγώνων, του μακροβιότερου θεσμού της αρχαιότητας. Εκτός από τα αρχαία έργα τέχνης ο επισκέπτης έχει τη δυνατότητα λεπτομερειακής πληροφόρησης μέσα από το πλούσιο εποπτικό υλικό.','Αρχαία Ολυμπία', 	geography::STPointFromText('POINT(30.26240 29.119)', 4326), 'Καθημερινά 08:30 - 15.30', 10, 4, 5, 2);
	('Αρχαιολογικό Μουσείο Αλεξανδρούπολης', 	'Στη μόνιμη έκθεσή του παρουσιάζονται ευρήματα που αφηγούνται την ιστορία της περιοχής από τους προϊστορικούς έως και τους ρωμαϊκούς χρόνους και προέρχονται από το χερσαίο τμήμα της Περιφερειακής Ενότητας Έβρου.', 'Λεωφόρος Μάκρης 44, Αλεξανδρούπολη 68131', 	geography::STPointFromText('POINT(40.8476 25.856)', 4326), '(+30)2551026103)', 'από 1/11 έως 31/3 Δευτέρα, Τετάρτη - Κυριακή: 8:30-15:30', 1, 11, 4, 4);
	('Αρχαιολογικό Μουσείο Αβδήρων', 'To Αρχαιολογικό Μουσείο Αβδήρων παρουσιάζει την εξέλιξη της πόλης των Αβδήρων από την ίδρυσή της τον 7ο αι. π.Χ. μέχρι τον 12ο αι. μ.Χ. Τα εκθέματά του μαρτυρούν τόσο τον τρόπο ζωής και τον πολιτισμό των αρχαίων Αβδηριτών όσο και τον ρόλο που διαδραμάτισε η πόλη στην περιοχή της Θράκης αλλά και στον αρχαίο ελληνικό κόσμο.', 'Δ. Λαζαρίδη 2, ’βδηρα 67061', geography::STPointFromText('POINT(40,9810199 24,9537921)', 4326),  '2541051003', 'από 1/11 έως 31/3', 1, 8, 3, 5);
	go
	('Αρχαιολογικό Μουσείο Δράμας','Τα αρχαιολογικά ευρήματα καταγράφουν την πολιτιστική ιστορία της Δράμας και της περιοχής από τους νομάδες κυνηγούς Μέσης Παλαιολιθικής Εποχής 50.000 π.Χ., ως τους πρώτους γεωργούς και κτηνοτρόφους των Νεολιθικών κοινοτήτων (5.500-3.000 π.Χ.) και από τις πρώτες πατριαρχικές κοινωνίες της Εποχής του Χαλκού (3.000-1.050 π.Χ.) ως τα ισχυρά γένη της Πρώιμης Εποχής του Σιδήρου (1.050-700 π.Χ.).','Πατριάρχου Διονυσίου 2, Δράμα 66133', 	geography::STPointFromText('POINT(41.1479538 24.1433015)', 4326), 	'(+30) 2521031365)','Δευτέρα έως Κυριακή 08:00-15:00','1','2','3'),
('Αρχαιολογικό Μουσείο Καβάλας','Στην έκθεσή του παρουσιάζεται η ιστορία της Καβάλας από την ίδρυσή της τον 7ο αι π.Χ. (Νεάπολη) έως το τέλος των βυζαντινών χρόνων (Χριστούπολη), καθώς και της ευρύτερης περιοχής της.','Ερυθρού Σταυρού 17, Καβάλα 65110', geography::STPointFromText('POINT(40.93570225 24.4037281081839)', '(+30) 2510222335)','από 1/11 έως 31/3 08:30  15:30, Τρίτη κλειστό','1','12','5'),
('Εθνικό Αρχαιολογικό Μουσείο','Το Εθνικό Αρχαιολογικό Μουσείο ιδρύθηκε το 1893 και στεγάζεται σε νεοκλασικό κτήριο που οικοδομήθηκε μεταξύ των ετών 1866 και 1889. Είναι το μεγαλύτερο μουσείο της Ελλάδας και ένα από τα σημαντικότερα στον κόσμο. Οι πλούσιες συλλογές του, που αριθμούν περισσότερα από 11.000 εκθέματα, προσφέρουν στον επισκέπτη ένα πανόραμα του αρχαίου ελληνικού πολιτισμού από τις αρχές της προϊστορίας έως την ύστερη αρχαιότητα.','28ης Οκτωβρίου (Πατησίων) 44, Αθήνα 10682', geography::STPointFromText('POINT(37.98899787540997 23.733011484146118)', 4326), '(+30) 2132144800)','από 1/11 έως 31/3 08:30 - 15:30, Τρίτη 13:00 - 20:00','1','10','5'),
('Αρχαιολογική Συλλογή Αεροδρομίου Αθηνών','Περιλαμβάνει ευρήματα που ήρθαν στο φως από τις ανασκαφές στον χώρο του Αεροδρομίου, με αφορμή την κατασκευή του. Χρονολογούνται από την 3η χιλιετία π.Χ. έως και τη μεταβυζαντινή περίοδο παρουσιάζοντας την ιστορία των Μεσογείων, μιας κατεξοχήν αγροτικής περιοχής της Αττικής.','Σπάτα, Αττική 1900','37.93732705','23.9465284918041','(+30) 2103530000)','1','3','5'),
('Ναυτικό Μουσείο Ελλάδoς','Στος 10 μεγάλες αίθουσες που έχουν συνολική επιφάνεια περίπου 1850 τ.μ. παρουσιάζεται με ανάγλυφο τρόπο η ναυτική πορεία του ελληνισμού μέσα στους αιώνες. Στην πρώτη στεγάζεται η Ναυτική Πινακοθήκη, με έργα των σημαντικότερων θαλασσογράφων του 19ου και 20ου αιώνα (Προσαλέντη-Βολανάκη-Χατζή κ.ά.). Στη συνέχεια παρουσιάζεται η ναυτική δραστηριότητα των Ελλήνων στην προϊστορία, στην αρχαιότητα, στη βυζαντινή και μεταβυζαντινή περίοδο ως το τέλος του 18ου αιώνα, στους Βαλκανικούς Πολέμους και από τον Β Παγκόσμιο Πόλεμο έως τις μέρες μας. Παράλληλα, δίνεται έμφαση στη μετάβαση από την ιστιοφόρο ναυτιλία στην ατμοπλοΐα. Οι τελευταίες εκθεσιακές ενότητες αφορούν την ιστορία και εξέλιξη του εμπορικού ναυτικού.', 'Ακτή Θεμιστοκλέους, Φρεαττύς, Τ.Κ. 185 37, Πειραιάς (Νομός Αττικής)', '37.928152', '23.637894', '+30 210 4516264, 4286959', 'Δευτέρα, Κυριακή και αργίες κλειστά. Τρίτη - Σάββατο: 09.00 - 14.00', '8', '15', '4'),
('Ναυτικό Μουσείο Αιγαίου','Στα εκθέματα του Μουσείου περιλαμβάνονται ομοιώματα πλοίων από την προ-Μινωϊκή περίοδο μέχρι τις αρχές του 20ού αιώνα, ιστορικά ναυτιλιακά έγγραφα, σπάνια χαρακτικά και χάρτες, αρχαία αντικείμενα, ναυτικά όργανα, εξαρτήματα και εργαλεία, καθώς και νομίσματα με ναυτικά θέματα από τον 5ο αιώνα π.Χ..','Ενόπλων Δυνάμεων 10, Μύκονος (Νομός Κυκλάδων)','37.44527903074070','25.32839621717900'.'+30 22890 22700 , 210 8125547','Από 1η Απριλίου έως 30 Νοεμβρίου Τρίτη με Κυριακή: 10:00-13:00 & 18:00  21:00','8','15','4'),
('Νομισματικό Μουσείο','Το Νομισματικό Μουσείο είναι ένα από τα παλαιότερα κρατικά μουσεία της Ελλάδος. Θεσμοθετήθηκε το 1834, την ίδια χρονιά με το Εθνικό Αρχαιολογικό Μουσείο. Η αρχαιολατρία της εποχής διαδεδομένη σε όλη την Ευρώπη και η πρόσφατη ίδρυση του νεοελληνικού κράτους δημιούργησαν τις συνθήκες της προστασίας της εθνικής πολιτιστικής κληρονομιάς. Έτσι, από την αρχή, η ιστορία του Μουσείου συνδέθηκε άμεσα με την ιστορία του νεοελληνικού κράτους, τις κοινωνικές συνθήκες και τις πολιτιστικές κατευθύνσεις της κάθε εποχής. Καθοριστικό ρόλο στην πορεία του όμως, έπαιξε και η προσωπικότητα των ανθρώπων που το διεύθυναν.','Ελ. Βενιζέλου 12, Αθήνα 106 71','37.97775645'.'23.735391134','	Από 1 Νοεμβρίου 2024 έως 31 Μαρτίου 2025 Τετάρτη  Δευτέρα: 08.30  15.30 Τρίτη κλειστό','13','14','3'),
('Μουσείο Επιστημών και Τεχνολογίας Πανεπιστημίου Πατρών','Το ΜΕΤ ασχολείται με τη συγκρότηση συλλογών και την τεκμηρίωσή τους με σκοπό την αξιοποίησή τους σε εκθεσιακούς χώρους αλλά και εκπαιδευτικά προγράμματα. Η τεκμηρίωση των συλλογών περιλαμβάνει ταύτιση και περιγραφή κάθε αντικειμένου, καθώς και στοιχεία σχετικά με την προέλευσή τους. Το υλικό το οποίο συγκροτεί και εμπλουτίζει τις συλλογές του Μουσείου αποτελείται από σκεύη, εργαλεία, μηχανήματα, τεχνολογικούς εξοπλισμούς, αρχειακό και φωτογραφικό υλικό, χάρτες, βιβλία και άλλο συνοδευτικό υλικό. Το υλικό του Μουσείο προέρχεται κυρίως από δωρεές. Για τον εμπλουτισμό των συλλογών το ΜΕΤ είναι σε επαφή με φορείς οι οποίοι μπορούν να βοηθήσουν. Καλεί επίσης όλους τους φορείς και τα άτομα που έχουν στην κατοχή τους σχετικό υλικό ή γνωρίζουν την ύπαρξη του να συμβάλλουν στην προσπάθεια του Μουσείου.','Πανεπιστημιούπολη Πατρών 265 04','38.28777600591320'.'21.78474302675890'.'Δευτέρα - Παρασκευή 9:00 - 14:00','12','15','3'),
('Κέντρο Ιστορικής Διαδρομής Εβραϊσμού Θεσσαλονίκης','Το Κέντρο περιλαμβάνει εξειδικευμένη βιβλιοθήκη με μελέτες για την ιστορία, λαογραφία, γλωσσολογία του εβραϊσμού της Ελλάδος. Επίσης δισκοθήκη-κασσετοθήκη με λαϊκά και δημοτικά τραγούδια των Ελλήνων Εβραίων. Ακόμη διαρκή έκθεση 300 φωτογραφιών και χαρτών της περιόδου 1880-1943, με θέματα από την ζωή των Εβραίων της Θεσσαλονίκης, και μικρή πτέρυγα μουσείου, όπου εκτίθενται εβραϊκά λατρευτικά αντικείμενα, παραδοσιακές ενδυμασίες, παλαιές εβραϊκές εφημερίδες και εκδόσεις.','Βασ. Ηρακλείου 26, Τ.Κ. 54624, Θεσσαλονίκη (Νομός Θεσσαλονίκης)','40.63450691337600'.'22.94146102366860'.'	+30 2310 2279063, 223231'.'Από 1η Νοεμβρίου έως 31 Μαρτίου 2010  08:30-15:00'.'14'.'14'.'5'),
('Λευκός Πύργος: Μουσείο','Ο Λευκός Πύργος, το εμβληματικό μνημείο της Θεσσαλονίκης, φιλοξενεί από τις 5 Σεπτεμβρίου 2008 έκθεση αφιερωμένη στη Θεσσαλονίκη, την πόλη που δεν έπαψε ποτέ να είναι πόλη στη μακραίωνη ιστορική παρουσία της και που ήταν ανοιχτή στη συνύπαρξη ανθρώπων με διαφορετική καταγωγή, θρησκεία και κουλτούρα. Η έκθεση διατρέχει συνοπτικά και με πανοραμική ματιά την ιστορία της πόλης από την ίδρυσή της το 316/15 π.Χ. μέχρι τις μέρες μας. Ο στόχος της δεν είναι να υποκαταστήσει τα άλλα μουσεία της πόλης, αντιθέτως, φιλοδοξεί να «συστήσει» τη Θεσσαλονίκη κυρίως στους κατοίκους της και να κεντρίσει το ενδιαφέρον τους για παραπέρα γνωριμία μέσα από τα θεματικά μουσεία και τα μνημεία της.','Παλαιά παραλία Θεσσαλονίκης, Θεσσαλονίκη (Νομός Θεσσαλονίκης)','40.6263653936045'.'22.948334258804600','14','12','5'),
('Μουσείο και Κέντρο Μελέτης του Ελληνικού Θεάτρου','Στις αίθουσες του εκτίθενται τεκμήρια της θεατρικής ζωής, σκηνικά κοστούμια και σκηνικά αντικείμενα, φωτογραφίες, μακέτες, προσωπικά αντικείμενα σημαντικών ανθρώπων του θεάτρου, αφίσες και προγράμματα. Η βιβλιοθήκη περιλαμβάνει περί τους 25.000 τόμους για το θέατρο από την ελληνική και ξένη βιβλιογραφία. Το δε θεατρικό αρχείο, χειρόγραφα του 18ου αιώνα, προγράμματα και αφίσες από θιάσους από τον 19ο αιώνα.','Ακαδημίας 50, Τ.Κ. 10679, Αθήνα (Νομός Αττικής)','37.98121474553460'.'23.73493270002200','+30 210 3629430, 3637453','Από 1η Νοεμβρίου έως 31 Μαρτίου 2010: 08:30-15:00','9','15','3'),
('Μουσείο της Ελιάς και του Ελληνικού Λαδιού','Το Μουσείο της Ελιάς και του Ελληνικού Λαδιού στη Σπάρτη έχει ως κύριο σκοπό να αναδείξει τον πολιτισμό και την τεχνολογία της ελιάς και της ελαιοπαραγωγής, που συνδέεται άρρηκτα με την ελληνική, και γενικότερα τη μεσογειακή ταυτότητα. Είναι μοναδικό στο είδος του στην Ελλάδα και βρίσκεται στην καρδιά της Λακωνίας, μιας από τις κύριες ελαιοπαραγωγούς περιοχές της χώρας μας.','Όθωνος - Αμαλίας 129, Τ.Κ. 23100, Σπάρτη (Νομός Λακωνίας)','37.07058133628890'.'22.426240192926200'.'+30 27310 89315, +30 210 3256922-3'.'16 Οκτωβρίου-28 Φεβρουαρίου: 10.00- 17.00 1η Μαρτίου-15 Οκτωβρίου: 10:00-18:00'.'11'.'15'.'3'),
('Αρχαιολογικό Μουσείο Καστελλόριζου','Το Αρχαιολογικό Μουσείο Καστελλόριζου στεγάζεται από το 1984 στο "Κονάκι" που αποτελεί τμήμα του μεσαιωνικού Κάστρου του Αγ. Νικολάου. Βρίσκεται στη συνοικία "Κάβος" και πρόκειται για διώροφο κτίσμα, του οποίου το ισόγειο ανάγεται στα χρόνια της Ιπποτοκρατίας, ενώ ο όροφος αποτελεί προσθήκη του 19ου αι., εποχή Τουρκοκρατίας.','Μεγίστη (Νομός Δωδεκανήσου)','36.15155701064380','29.593552924569500','+30 22460 49283','Τετάρτη ως και Δευτέρα 08:30-15:30','2','12','5'),
('Βυζαντινή και Μεταβυζαντινή Συλλογή Χανίων','Το πλούσιο αρχαιολογικό υλικό, το οποίο έχει συγκεντρωθεί από τις ανασκαφές που επί πολλά χρόνια διενεργεί η 13η Εφορεία Βυζαντινών Αρχαιοτήτων στο νομό Χανίων, αλλά και από περισυλλογές και δωρεές, αποτέλεσε μια Συλλογή στην οποία καταγράφεται με σαφήνεια η ιστορική πορεία του δυτικότερου νομού της Κρήτης από τα παλαιοχριστιανικά χρόνια έως και την τουρκοκρατία. Αντιπροσωπευτικά δείγματα αυτής της Συλλογής εκτίθενται στο ναό του San Salvatore.','Θεοτοκοπούλου 78, Τ.Κ. 73131, Χανιά (Νομός Χανίων)','35.51845693305560','24.015186969686500','+30 28210 96046','Από 1η Νοεμβρίου 2024 έως την 31η Μαρτίου 2025 08:30-15:30','2','12','4');
go

