INSERT INTO TIME_PERIODS (per_id, per_name, per_time_start, per_time_end, per_desc) VALUES 
(1, 'Παλαιολιθική περίοδος', '2.5 εκ. χρόνια π.Χ.', '10,000 π.Χ.', 'Η παλαιότερη φάση της προϊστορίας, όταν οι άνθρωποι ζούσαν ως κυνηγοί-τροφοσυλλέκτες.'),
(2, 'Μεσολιθική περίοδος', '10,000 π.Χ.', '8,000 π.Χ.', 'Μεταβατική περίοδος όπου αναπτύχθηκαν πρωτόγονα εργαλεία και τεχνικές καλλιέργειας.'),
(3, 'Νεολιθική περίοδος', '8,000 π.Χ.', '3,200 π.Χ.', 'Περίοδος σημαντικής εξέλιξης με την καθιέρωση της γεωργίας και των μόνιμων οικισμών.'),
(4, 'Εποχή του Χαλκού', '3,200 π.Χ.', '1,200 π.Χ.', 'Περίοδος που χαρακτηρίζεται από την ευρεία χρήση του χαλκού για εργαλεία και όπλα.'),
(5, 'Εποχή του Σιδήρου', '1,200 π.Χ.', '800 π.Χ.', 'Η εποχή όπου ο σίδηρος αντικατέστησε τον χαλκό ως κύριο υλικό για κατασκευή εργαλείων.'),
(6, 'Αρχαϊκή περίοδος', '800 π.Χ.', '480 π.Χ.', 'Η περίοδος διαμόρφωσης των ελληνικών πόλεων-κρατών και της πρώτης γραπτής λογοτεχνίας.'),
(7, 'Κλασική περίοδος', '480 π.Χ.', '323 π.Χ.', 'Η χρυσή εποχή της αρχαίας Ελλάδας, με μεγάλη ακμή στις τέχνες, τη φιλοσοφία και τη δημοκρατία.'),
(8, 'Ελληνιστική περίοδος', '323 π.Χ.', '30 π.Χ.', 'Περίοδος εξάπλωσης του ελληνικού πολιτισμού υπό την κυριαρχία των επιγόνων του Μεγάλου Αλεξάνδρου.'),
(9, 'Ρωμαϊκή περίοδος', '30 π.Χ.', '330 μ.Χ.', 'Περίοδος κατά την οποία η Ελλάδα βρέθηκε υπό ρωμαϊκή κυριαρχία.'),
(10, 'Βυζαντινή περίοδος', '330 μ.Χ.', '1453 μ.Χ.', 'Η περίοδος του Ανατολικού Ρωμαϊκού Κράτους με έδρα την Κωνσταντινούπολη.'),
(11, 'Οθωμανική περίοδος', '1453 μ.Χ.', '1821 μ.Χ.', 'Περίοδος κατά την οποία η Ελλάδα ήταν μέρος της Οθωμανικής Αυτοκρατορίας.'),
(12, 'Νεότερη Ελληνική περίοδος', '1821 μ.Χ.', 'σήμερα', 'Η περίοδος από την Ελληνική Επανάσταση έως τη σύγχρονη εποχή.');
